def hamming_distance(seq1, seq2):
    return sum (1 for x,y in zip(seq1,seq2) if x != y)


def Pattern_String_Distance(pattern, Dna):
    distance = 0
    k = len(pattern)
    for string in Dna:
        Hamming_dis = float("inf")

        for i in range(len(string)-k +1):
            kmer = string[i:i+k]
            current_distance = hamming_distance(pattern, kmer)

            if current_distance < Hamming_dis:
                Hamming_dis = current_distance
        distance = distance + Hamming_dis
    return distance


